mapper.update:
		UPDATE ouruser.tbl_hiring
    		SET btitle = #{btitle}, 
        			bcontent = #{bcontent}, 
      				bendDate = #{bendDate}
   		 WHERE bno = #{bno}

UPDATE ouruser.tbl_hiring
		SET bappcnt= bappcnt+ 1
WHERE bno = #{bno}

		UPDATE ouruser.tbl_hiring
  			  SET bblind = 1
   		 WHERE bno = #{bno}
